// working with/using/importing java libraries in groovy
import java.io.*;

//way to know all the methos of any class (testrunner)
log.info testRunner.metaClass.methods*.name.unique().sort()

class Hello{

	def log;
	def context;
	def testRunner;

	def Hello(log, context, testRunner){
		this.log = log
		this.context = context
		this.testRunner = testRunner		
	}

	def sayHello(String name){
		//concatinaion
		log.info ("   Hello " + name + "!");
 	}

}

context.setProperty("Hello", new Hello(log,context,testRunner))

//calling and storing "Hello" class (or teststep)
def testStep = testRunner.testCase.getTestStepByName("Hello")

//executing the class
testStep.run(testRunner, context)

//accessing methods in the class
context.Hello.sayHello("Shreyata")

//calling and storing "Hello" class (or teststep)
project = testRunner.testCase.testSuite.project ;
testcase = project.testSuites["Groovy Scripting"].testCases["Working with Class and methods"] ; 
hellostep = testcase.getTestStepByName("Hello");

//executing the class
hellostep.run(testRunner, context)

//accessing methods in the class
context.Hello.sayHello("Shreyata")

def ref = context.Hello;
ref.sayHello("World");

//Groovy - run request from same TestCase
def status = testRunner.runTestStepByName("SampleStep")
def result = status.getStatus().toString();
log.info ("   ---- "+result)

//Groovy - run request from another TestCase or TestSuite
project = testRunner.testCase.testSuite.project ;
tcase = project.testSuites["SupportingTestSuite"].testCases["SupportingRunTest"] ; 
tstep = tcase.getTestStepByName("SupportingSampleStep");
def status = tstep.run(testRunner, context)
def result = status.getStatus().toString();
log.info ("   ----   "+result)

if (result == "OK"){
	log.info " Test Passed!!! "
}else {
	log.infor " Test Failed! "
}

