//Evaluating the expression value 
switch(a) {            
	//There is case statement defined for 4 cases 
	// Each case statement section has a break condition to exit the loop 

	case 1: 
	  log.info("The value of a is One"); 
	  break; 
	case 2: 
	  log.info("The value of a is Two"); 
	  break; 
	case 3: 
	  log.info("The value of a is Three"); 
	  break; 
	case 4: 
	  log.info("The value of a is Four"); 
	  break; 
	default: 
	  log.info("The value is unknown"); 
	  break; 
}

